import { Component, OnInit } from "@angular/core";
import { NavigationEnd, Router } from "@angular/router";
import { getRandomBackground } from "src/app/common/util/utils";
import { BackgroundService } from "../common/services/background.service";

@Component({
  selector: "app-wow",
  templateUrl: "./wow.component.html",
  styleUrls: ["./wow.component.scss"],
})
export class WowComponent implements OnInit {
  bgImg = getRandomBackground(this.router.url, 2);
  constructor(
    private router: Router,
    private backgroundService: BackgroundService
  ) {}
  ngOnInit(): void {
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.backgroundService.getRandomBackground(this.router.url, 2);
      }
    });
  }
}
