import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'wow-button',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.scss']
})
export class ButtonComponent implements OnInit{
  @Input() label: string = '';
  @Input() width: string = '';

  colorBtn = '';

  customWidth(width : string) {
    return 'width: '+width;
  }

  ngOnInit(): void {
    (this.getRandomColorBtn() == 1) ? this.colorBtn == 'background: #660204' : 'background: #1823BB'
  }
  getRandomColorBtn() {
    const number = Math.random();
    const result = Math.floor(number * 2) + 1;
    return result;
  }
}
