import { Component, OnInit } from "@angular/core";
import {  NavigationEnd, Router } from "@angular/router";

@Component({
  selector: "app-wow",
  templateUrl: "./wow.component.html",
  styleUrls: ["./wow.component.scss"],
})
export class WowComponent implements OnInit {
  bgImg = this.getRandomBackground("home", 2);;
  constructor(private router: Router) {}
  ngOnInit(): void {
    this.router.events.subscribe((event) => {
      if(event instanceof NavigationEnd) {
        switch (this.router.url) {
          case "/home":
            this.bgImg = this.getRandomBackground("home", 2);
            break;
          case "/battlegrounds":
            this.bgImg = this.getRandomBackground("battlegrounds", 1);
        }
      }
    })


  }
  getRandomBackground(type: string, maxRange: number) {
    const number = Math.random();
    const result = Math.floor(number * maxRange) + 1;
    return `/assets/img/backgrounds/${type}/${result}.png`;
  }
}
