import { Component, OnInit } from "@angular/core";
import { NavigationEnd, Router } from "@angular/router";
import { BackgroundService } from "../common/services/background.service";

@Component({
  selector: "app-wow",
  templateUrl: "./wow.component.html",
  styleUrls: ['./wow.component.scss']
})
export class WowComponent implements OnInit{

  routeChange = false;

  constructor (private router: Router, private backgroundService: BackgroundService) {}
  bgImg = this.backgroundService.getRandomBackground(this.router.url, 2);

  ngOnInit(): void {

    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.routeChange = true;
      }
      setTimeout(()=>{
        this.routeChange = false;
      },3400)
    });
  }

}
<wow-header/>
<div class="page" >
  <div *ngIf="!routeChange">
    <router-outlet></router-outlet>
  </div>
  <div class="align page" [class.not-allowed]="routeChange" *ngIf="routeChange" style="background-image: url('/assets/img/backgrounds/loader/loading.png');">
    <wow-loading/>
  </div> 
</div>
